package com.cheersondemand.frameworks.retrofit;import com.cheersondemand.model.LoginRequest;import com.cheersondemand.model.SignUpRequest;import com.cheersondemand.model.AuthenticationResponse;import com.cheersondemand.model.SocialLoginRequest;import retrofit2.Call;import retrofit2.http.Body;import retrofit2.http.POST;/** * Created by abhishekkumar on 2/7/18. */public interface WebServices {    @POST("/cheers_on_demand/api/v1/users")    Call<AuthenticationResponse> signUpUsingEmail(@Body SignUpRequest signUpRequest);    @POST("/cheers_on_demand/api/v1/oauth/token")    Call<AuthenticationResponse> loginUsingEmail(@Body LoginRequest loginRequest);    @POST("/cheers_on_demand/api/v1/oauth/token")    Call<AuthenticationResponse> loginUsingSocial(@Body SocialLoginRequest socialLoginRequest);}